import React, { useState } from 'react';
import { useMutation } from 'react-apollo-hooks';
import gql from 'graphql-tag';
import Layout from '../common/Layout';
import Input from '../common/Input'
import useForm from '../hooks/useForm';
import authHOC from '../utils/authHOC';

const CREATE_POST = gql`
    mutation createPost($data:PostInput!){
        createNewPost(data:$data){
            _id
        }
    }
`;

function Create({ history }) {
    const [sendPost] = useMutation(CREATE_POST);
    const [cover, setCover] = useState('');
    const [coverPreview, setCoverPreview] = useState('');

    const catchCover = event => {
        const reader = new FileReader();
        const file = event.target.files[0];

        reader.onloadend = () => {
            setCover(file);
            setCoverPreview(reader.result);
        }

        reader.readAsDataURL(file);

    }

    const catchData = async (inputs) => {
        const { data, errors } = await sendPost({ variables: { data: { ...inputs, cover } } });
        if (data) history.push('/');
        if (errors) alert(`Errores: ${errors}`);
    }

    const {
        inputs,
        handleSubmit,
        handleInputChange,
    } = useForm(catchData);

    return (<>
        <Layout head="Tu carrito de compras."
            subhead="AquÃ­ estan tus " >
            <div className="container">
                <div className="row">
                    <div className="col-lg-8 col-md-10 mx-auto">
                        <form onSubmit={handleSubmit}>
                            <Input
                                name="title"
                                label="Titulo: "
                                type="text"
                                placeholder="Titulo del post"
                                value={inputs.title}
                                change={handleInputChange}
                                required={true}
                            ></Input>
                            <div className="control-group">
                                <div className="form-group floating-label-form-group controls">
                                    <label>Contenido: </label>
                                    <textarea className="form-control"
                                        placeholder="Contenido del post "
                                        name="content" onChange={handleInputChange} value={inputs.content}
                                        cols="30" rows="10"></textarea>
                                </div>
                            </div>
                            <Input
                                name="cover"
                                label="Cover: "
                                type="file"
                                placeholder="Selecciona el cover de tu post."
                                change={catchCover}
                                required={true}
                            ></Input>
                            <img src={coverPreview} alt="" className="d-block w-50"></img>

                            <div className="clearfix mt-4">
                                <button className="btn btn-primary float-right" type="submit">
                                    Guardar post.
                        </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </Layout>
    </>);


    {

        state = {
            platillos: [],
            searchValue: ""
        };

        componentDidMount(); {
            this.getPlatillos()

        }

        getPlatillos() ;{
            axios.get(`https://tamales-server.herokuapp.com/all/food`)
                .then(response => {
                    this.setState({
                        platillos: response.data,
                    })
                })
                .catch(err => console.log(err))
        }

        borrarPlatillo(id) ;{
            axios.put(`https://tamales-server.herokuapp.com/update/food${id}`)
                .then(res => {
                    this.getPlatillos()
                })
                .catch(err => console.log(err))
        }

        buscaPlatilos = (e) => {
            this.setState({
                searchValue: e.target.value.toLowerCase()
            })
        }


        /*  function myFunction() {
             <div class="alert alert-warning alert-dismissible fade show" role="alert">
             <strong>Tu platillo se ah AÃ±adido a tu Carrito de Compra</strong> Si deseas Ordenar solo eso dirigete a tu Carrito 
             <button type="button" class="close" data-dismiss="alert" aria-label="Close">
               <span aria-hidden="true">&times;</span>
             </button>
           </div>
         } */

        renderPlatillos = () => {
            return this.state.platillos.map(platillo => {
                if (platillo.platillo.toLowerCase().startsWith(this.state.searchValue)) {
                    return (
                        <div className="card" >
                            <img src={platillo.img_platillo} style={{ width: '100%' }} className="imgprro card-img-top" alt="Platillo Recomendado" />
                            <div className="card-body">
                                <h5 className="card-title">{platillo.platillo}</h5>
                                <h5 className="card-text">{platillo.picante}</h5>
                                <img className="logoRest" src={platillo.restaurante} alt="restaurante" />
                                <p className="card-text">{platillo.descripcion}</p>
                                <div>
                                    <button type="button" class="btn btn-outline-success"><Link className="nav-link" to="/create">Carrito</Link></button>
                                </div>
                            </div>
                        </div>
                    )
                }
            })

        }

        render(); {
            return (
                <div className='card-container'>
                    <div className="input-group mb-3">
                        <input type="text" className="form-control" placeholder="Â¿De que tienes antojo?" onChange={this.buscaPlatilos} />
                    </div>
                    {
                        this.renderPlatillos()
                    }
                </div>
            )

        }
    }
}

export default (Create);
