import React, { useState } from 'react';
import { useMutation } from 'react-apollo-hooks';
import gql from 'graphql-tag';
import Layout from '../common/Layout';
import Input from '../common/Input'
import useForm from '../hooks/useForm';
import authHOC from '../utils/authHOC';
import { Link } from 'react-router-dom';


const CREATE_POST = gql`
    mutation createPost($data:PostInput!){
        createNewPost(data:$data){
            _id
        }
    }
`;

function Create({ history }) {
    const [sendPost] = useMutation(CREATE_POST);
    const [cover, setCover] = useState('');
    const [coverPreview, setCoverPreview] = useState('');

    const catchCover = event => {
        const reader = new FileReader();
        const file = event.target.files[0];

        reader.onloadend = () => {
            setCover(file);
            setCoverPreview(reader.result);
        }

        reader.readAsDataURL(file);

    }

    const catchData = async (inputs) => {
        const { data, errors } = await sendPost({ variables: { data: { ...inputs, cover } } });
        if (data) history.push('/');
        if (errors) alert(`Errores: ${errors}`);
    }

    const {
        inputs,
        handleSubmit,
        handleInputChange,
    } = useForm(catchData);

    return (<>
        <Layout head="Tu carrito de compras."
            subhead="Aqu√≠ estan tus Antojitos" >
            <div classNameName="container">
                <div className="row" style={{ background: 'rgba(255, 255, 255, 0.8)' }}>
                    <div className="col-lg-8 col-md-10 mx-auto">
                        <form onSubmit={handleSubmit}>
                        </form>
                    </div>
                </div>
            </div>

        </Layout>
        <div>
            <div class="card mb-3">
                <img src="https://m.lacasadetono.com.mx/wp-content/uploads/2019/07/Enfrijoladas-1.png" class="card-img-top" alt="..." />
                <div class="card-body">
                    <h5 class="card-title">Enfrijoladas</h5>
                    <p class="card-text">Ideal para compartir, tres tortillas perfectamente dobladas y rellenas de pollo desmenuzado y ba√±adas con una salsa de frijol que muchos imitan pero no se acercan al sabor.</p>
                    <p class="card-text"><small class="text-muted">Tortillas, Queso, Frijol, Crema, Pollo</small></p>
                    <button type="button" class="btn btn-success">
                        <Link to='onRoute'>
                            <p>$50 Comprar</p>
                        </Link>
                    </button>
                    <button type="button" class="btn btn-danger">Borrar</button>
                </div>
            </div>

        </div>
    </>);


}

export default (Create);

