import React from 'react';
import { useMutation } from 'react-apollo-hooks';
import gql from 'graphql-tag';
import Layout from '../common/Layout';
import Input from '../common/Input'
import useForm from '../hooks/useForm';

const CREATE_MUTATION = gql`

    mutation AddAuthor($data:AuthorInput!){
        createNewAuthor(data:$data){
            _id,
            email
            first_name
        }
    }

`;

function Signup({history}) {

    const [sendSignup] = useMutation(CREATE_MUTATION);

    const catchData = async (inputs) => {
        if ( inputs.password === inputs.confirm_password){
            delete inputs.confirm_password;
            console.log(inputs);
            const { data } = await sendSignup({variables:{data:{...inputs}}});
            if(data){
                if(data.errors) console.log(data.errors);
                history.push('/login');
            }
        } else {
            alert('Tu contraseÃ±a no coincide.');
        }
    }

    const {
        inputs,
        handleSubmit,
        handleInputChange,
    } = useForm(catchData);

    return(<>
        <Layout head = "Crea tu cuenta."
        subhead = "Comencemos a cumplir tus antojos, crea una cuenta con nosotros." >
        <div className="container">
            <div className="row">
            <div className="col-lg-8 col-md-10 mx-auto">
                <form onSubmit={handleSubmit}>
                    <Input
                    name="first_name"
                    label="Nombre: "
                    type="text"
                    placeholder="Escribe tu nombre"
                    value={inputs.first_name}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="last_name"
                    label="Apellido: "
                    type="text"
                    placeholder="Escribe tu apellido"
                    value={inputs.last_name}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="email"
                    label="Email: "
                    type="text"
                    placeholder="Escribe tu email"
                    value={inputs.email}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="password"
                    label="Password: "
                    type="password"
                    placeholder="Escribe tu password"
                    value={inputs.password}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="confirm_password"
                    label="Confirma tu Password: "
                    type="password"
                    placeholder="Escribe de nuevo tu password"
                    value={inputs.confirm_password}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="birth_date"
                    label="Fecha de nacimiento "
                    type="Date"
                    placeholder="Ingresa tu fecha de nacimiento"
                    value={inputs.birth_date}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="gender"
                    label="Genero"
                    type="String"
                    placeholder="Genero"
                    value={inputs.gender}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                    <Input
                    name="addres"
                    label="Ingresa tu direccion: "
                    type="text"
                    placeholder="Ingresa tu calle y numero exterior"
                    value={inputs.addres}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                     <Input
                    name="cp"
                    label="Codigo Postal "
                    type="Number"
                    placeholder="CP:00000"
                    value={inputs.confirm_password}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                     <Input
                    name="Numero de celular"
                    label="Ingresa tu numero de celular "
                    type="Number"
                    placeholder="Ingrea un numero donde podamos contactarte"
                    value={inputs.confirm_password}
                    change={handleInputChange}
                    required={true}
                    ></Input>
                <div className="clearfix mt-4">
                    <button className="btn btn-primary float-right" type="submit">
                        Registrar 
                        </button>
                </div>
                </form>
            </div>
            </div>
        </div>
        </Layout>
    </>);
}

export default Signup;
